sys_script_include.script:
var NeedUtils = Class.create();
NeedUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate) {
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		if (testdate.before(rightnow)) {
			return true;
		} else {
			return false;
		}
	},
	isDateToday: function(stDate) {
		var rightnow = GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		if (testdate.compareTo(rightnow) == 0) {
			return true;
		} else {
			return false;
		}
	},
    type: 'NeedUtils'
};
